import React, { useState, useEffect, useRef } from 'react';
import { Stage, Layer, Line, Rect, Circle, Image, Transformer } from 'react-konva';
import io from 'socket.io-client';
// Fix import paths to be within src directory
import penIcon from '../assets/icons/pen.png';
import eraserIcon from '../assets/icons/eraser.png';
import lineIcon from '../assets/icons/line.png';
import rectangleIcon from '../assets/icons/rectangle.png';
import circleIcon from '../assets/icons/circle.png';
import selectIcon from '../assets/icons/select.png';
import imageIcon from '../assets/icons/image.png';
import clearIcon from '../assets/icons/clear.png';

const socket = io('http://192.168.31.158:3001', {
  transports: ['websocket', 'polling'],
  reconnectionDelay: 1000,
  reconnectionAttempts: 10,
  forceNew: true
});

const Whiteboard = () => {
  const [lines, setLines] = useState([]);
  const [shapes, setShapes] = useState([]);
  const [selectedId, setSelectedId] = useState(null);
  const [tool, setTool] = useState('pen');
  const [isDrawing, setIsDrawing] = useState(false);
  const [startPos, setStartPos] = useState({ x: 0, y: 0 });
  const [selectedColor, setSelectedColor] = useState('#000000');
  const [selectedStrokeWidth, setSelectedStrokeWidth] = useState(5);
  const [showColorPicker, setShowColorPicker] = useState(false);
  const [showStrokePicker, setShowStrokePicker] = useState(false);
  const transformerRef = useRef();
  const stageRef = useRef();

  const colors = [
    '#000000', '#FF0000', '#00FF00', '#0000FF',
    '#FFFF00', '#FF00FF', '#00FFFF', '#800000',
    '#008000', '#000080', '#808000', '#800080',
    '#008080', '#808080', '#C0C0C0', '#FFFFFF'
  ];

  useEffect(() => {
    socket.on('draw-update', ({ lines, shapes }) => {
      setLines(lines);
      setShapes(shapes);
    });

    return () => {
      socket.off('draw-update');
    };
  }, []);

  useEffect(() => {
    if (selectedId) {
      const shape = shapes.find(s => s.id === selectedId);
      if (shape && transformerRef.current) {
        transformerRef.current.nodes([stageRef.current.findOne('#' + selectedId)]);
        transformerRef.current.getLayer().batchDraw();
      }
    }
  }, [selectedId, shapes]);

  const handleMouseDown = (e) => {
    const pos = e.target.getStage().getPointerPosition();
    setIsDrawing(true);
    setStartPos(pos);
  
    if (tool === 'pen') {
      const newLines = [...lines, { 
        tool, 
        points: [pos.x, pos.y],
        color: selectedColor,
        strokeWidth: selectedStrokeWidth 
      }];
      setLines(newLines);
      socket.emit('draw-update', { lines: newLines, shapes });
    } else if (tool === 'eraser') {
      const newLines = [...lines, { 
        tool, 
        points: [pos.x, pos.y],
        color: '#ffffff',
        strokeWidth: 20 
      }];
      setLines(newLines);
      socket.emit('draw-update', { lines: newLines, shapes });
    } else if (tool === 'select') {
      const clickedShape = e.target;
      if (clickedShape === e.target.getStage()) {
        setSelectedId(null);
        return;
      }
      if (clickedShape.getParent().className === 'Transformer') {
        return;
      }
      setSelectedId(clickedShape.id());
    }
  };

  const handleMouseMove = (e) => {
    if (!isDrawing) return;

    const stage = e.target.getStage();
    const point = stage.getPointerPosition();

    if (tool === 'pen' || tool === 'eraser') {
      const lastLine = lines[lines.length - 1];
      lastLine.points = lastLine.points.concat([point.x, point.y]);
      // Preserve the original color and stroke width
      const newLines = [...lines.slice(0, -1), {
        ...lastLine,
        points: lastLine.points,
        color: lastLine.color,
        strokeWidth: lastLine.strokeWidth
      }];
      setLines(newLines);
      socket.emit('draw-update', { lines: newLines, shapes });
    } else if (['line', 'rectangle', 'circle'].includes(tool)) {
      const newShapes = [...shapes];
      const index = newShapes.findIndex(s => s.id === 'temp');
      const shapeData = {
        id: 'temp',
        type: tool,
        x: startPos.x,
        y: startPos.y,
        width: point.x - startPos.x,
        height: point.y - startPos.y,
        color: selectedColor,
        strokeWidth: selectedStrokeWidth
      };
      
      if (index === -1) {
        newShapes.push(shapeData);
      } else {
        newShapes[index] = shapeData;
      }
      setShapes(newShapes);
      socket.emit('draw-update', { lines, shapes: newShapes });
    }
  };

  const handleMouseUp = () => {
    setIsDrawing(false);
    if (['line', 'rectangle', 'circle'].includes(tool)) {
      const newShapes = shapes.map(shape => 
        shape.id === 'temp' ? { ...shape, id: Date.now().toString() } : shape
      );
      setShapes(newShapes);
      socket.emit('draw-update', { lines, shapes: newShapes });
    }
  };

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (!file) return;
  
    if (!file.type.startsWith('image/')) {
      alert('Please upload an image file');
      return;
    }
  
    const reader = new FileReader();
    reader.onload = () => {
      const tempCanvas = document.createElement('canvas');
      const tempCtx = tempCanvas.getContext('2d');
      const img = new window.Image();
      
      img.onload = () => {
        // Set fixed width of 300px and calculate height to maintain aspect ratio
        const targetWidth = 300;
        const aspectRatio = img.height / img.width;
        const targetHeight = targetWidth * aspectRatio;
  
        // Set canvas size and draw image
        tempCanvas.width = targetWidth;
        tempCanvas.height = targetHeight;
        tempCtx.drawImage(img, 0, 0, targetWidth, targetHeight);
        
        const processedImageData = tempCanvas.toDataURL('image/png');
  
        const newShape = {
          id: Date.now().toString(),
          type: 'image',
          x: window.innerWidth / 4,
          y: window.innerHeight / 4,
          width: targetWidth,
          height: targetHeight,
          image: processedImageData,
          rotation: 0,
        };
  
        const newShapes = [...shapes, newShape];
        setShapes(newShapes);
        socket.emit('draw-update', { lines, shapes: newShapes });
      };
      img.src = reader.result;
    };
    reader.readAsDataURL(file);
  };

  const handleDragEnd = (e) => {
    const shape = e.target;
    const newShapes = shapes.map(s => {
      if (s.id === shape.id()) {
        if (s.type === 'circle') {
          return {
            ...s,
            x: shape.x() - s.width / 2,
            y: shape.y() - s.height / 2
          };
        }
        return {
          ...s,
          x: shape.x(),
          y: shape.y()
        };
      }
      return s;
    });
    setShapes(newShapes);
    socket.emit('draw-update', { lines, shapes: newShapes });
  };

  const handleTransformEnd = (e) => {
    const node = e.target;
    const scaleX = node.scaleX();
    const scaleY = node.scaleY();
    
    node.scaleX(1);
    node.scaleY(1);
    
    const newShapes = shapes.map(shape => 
      shape.id === node.id() ? {
        ...shape,
        x: node.x(),
        y: node.y(),
        width: Math.abs(node.width() * scaleX),
        height: Math.abs(node.height() * scaleY),
        rotation: node.rotation()
      } : shape
    );
    setShapes(newShapes);
    socket.emit('draw-update', { lines, shapes: newShapes });
  };

  return (
    <div>
      <div style={{ position: 'fixed', top: 10, left: 10, zIndex: 1 }}>
        <button onClick={() => setTool('pen')} style={{ padding: 5 }}>
          <img src={penIcon} alt="Pen" width="20" height="20" />
        </button>
        <button onClick={() => setTool('eraser')} style={{ padding: 5 }}>
          <img src={eraserIcon} alt="Eraser" width="20" height="20" />
        </button>
        <button onClick={() => setTool('line')} style={{ padding: 5 }}>
          <img src={lineIcon} alt="Line" width="20" height="20" />
        </button>
        <button onClick={() => setTool('rectangle')} style={{ padding: 5 }}>
          <img src={rectangleIcon} alt="Rectangle" width="20" height="20" />
        </button>
        <button onClick={() => setTool('circle')} style={{ padding: 5 }}>
          <img src={circleIcon} alt="Circle" width="20" height="20" />
        </button>
        <button onClick={() => setTool('select')} style={{ padding: 5 }}>
          <img src={selectIcon} alt="Select" width="20" height="20" />
        </button>
        
        {/* Color Picker */}
        <div style={{ display: 'inline-block', position: 'relative', marginLeft: 5 }}>
          <button 
            onClick={() => setShowColorPicker(!showColorPicker)}
            style={{ 
              backgroundColor: selectedColor, 
              width: 30, 
              height: 30,
              border: '2px solid #666'
            }}
          />
          {showColorPicker && (
            <div style={{
              position: 'absolute',
              top: '100%',
              left: 0,
              backgroundColor: 'white',
              border: '1px solid #ccc',
              padding: 5,
              display: 'grid',
              gridTemplateColumns: 'repeat(4, 1fr)',
              gap: 5,
              zIndex: 2,
            }}>
              {colors.map((color, i) => (
                <div
                  key={i}
                  style={{
                    width: 25,
                    height: 25,
                    backgroundColor: color,
                    cursor: 'pointer',
                    border: '1px solid #ccc'
                  }}
                  onClick={() => {
                    setSelectedColor(color);
                    setShowColorPicker(false);
                  }}
                />
              ))}
            </div>
          )}
        </div>

        {/* Stroke Width Picker */}
        <div style={{ display: 'inline-block', position: 'relative', marginLeft: 5 }}>
          <button 
            onClick={() => setShowStrokePicker(!showStrokePicker)}
            style={{ 
              width: 30, 
              height: 30,
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              border: '2px solid #666'
            }}
          >
            <div style={{
              width: '80%',
              height: selectedStrokeWidth,
              backgroundColor: 'black',
              margin: 'auto'
            }}/>
          </button>
          {showStrokePicker && (
            <div style={{
              position: 'absolute',
              top: '100%',
              left: 0,
              backgroundColor: 'white',
              border: '1px solid #ccc',
              padding: 5,
              display: 'flex',
              flexDirection: 'column',
              gap: 5,
              zIndex: 2,
            }}>
              {[2, 4, 6, 8, 10, 12, 14, 16].map((width) => (
                <div
                  key={width}
                  style={{
                    width: '100px',
                    height: '20px',
                    cursor: 'pointer',
                    border: '1px solid #ccc',
                    display: 'flex',
                    alignItems: 'center',
                    padding: '0 5px',
                  }}
                  onClick={() => {
                    setSelectedStrokeWidth(width);
                    setShowStrokePicker(false);
                  }}
                >
                  <div style={{
                    width: '100%',
                    height: width,
                    backgroundColor: 'black',
                  }}/>
                </div>
              ))}
            </div>
          )}
        </div>

        {/* Image Upload */}
        <div style={{ display: 'inline-block', position: 'relative', marginLeft: 5 }}>
          <input 
            type="file" 
            accept="image/*" 
            onChange={handleImageUpload}
            style={{ 
              width: '30px',
              height: '30px',
              opacity: 0,
              position: 'absolute',
              left: 0,
              top: 0,
              cursor: 'pointer',
              zIndex: 1
            }}
          />
          <button style={{ 
            width: '30px', 
            height: '30px',
            display: 'inline-flex',
            alignItems: 'center',
            justifyContent: 'center',
            border: '2px solid #666',
            padding: 5
          }}>
            <img src={imageIcon} alt="Upload Image" width="20" height="20" />
          </button>
        </div>

        <button onClick={() => {
          setLines([]);
          setShapes([]);
          socket.emit('draw-update', { lines: [], shapes: [] });
        }} style={{ padding: 5 }}>
          <img src={clearIcon} alt="Clear All" width="20" height="20" />
        </button>
      </div>
      <Stage
        ref={stageRef}
        width={window.innerWidth}
        height={window.innerHeight}
        onMouseDown={handleMouseDown}
        onMouseMove={handleMouseMove}
        onMouseUp={handleMouseUp}
        onTouchStart={handleMouseDown}
        onTouchMove={handleMouseMove}
        onTouchEnd={handleMouseUp}
      >
        <Layer>
          {lines.map((line, i) => (
            <Line
              key={i}
              points={line.points}
              stroke={line.color}
              strokeWidth={line.strokeWidth}
              tension={0.5}
              lineCap="round"
              lineJoin="round"
              globalCompositeOperation={
                line.tool === 'eraser' ? 'destination-out' : 'source-over'
              }
            />
          ))}
          {/* Rest of the shapes rendering */}
          {shapes.map((shape) => {
            if (shape.type === 'line') {
              return (
                <Line
                  key={shape.id}
                  id={shape.id}
                  points={[shape.x, shape.y, shape.x + shape.width, shape.y + shape.height]}
                  stroke={shape.color || selectedColor}
                  strokeWidth={shape.strokeWidth || selectedStrokeWidth}
                  draggable={tool === 'select'}
                  onClick={() => setSelectedId(shape.id)}
                  onTransformEnd={handleTransformEnd}
                />
              );
            } else if (shape.type === 'rectangle') {
              return (
                <Rect
                  key={shape.id}
                  id={shape.id}
                  x={shape.x}
                  y={shape.y}
                  width={shape.width}
                  height={shape.height}
                  rotation={shape.rotation}
                  stroke={shape.color || selectedColor}
                  strokeWidth={shape.strokeWidth || selectedStrokeWidth}
                  draggable={tool === 'select'}
                  onClick={() => tool === 'select' && setSelectedId(shape.id)}
                  onDragEnd={handleDragEnd}
                  onTransformEnd={handleTransformEnd}
                />
              );
            } else if (shape.type === 'circle') {
              return (
                <Circle
                  key={shape.id}
                  id={shape.id}
                  x={shape.x + shape.width / 2}
                  y={shape.y + shape.height / 2}
                  radius={Math.abs(shape.width) / 2}
                  stroke={shape.color || selectedColor}
                  strokeWidth={shape.strokeWidth || selectedStrokeWidth}
                  draggable={tool === 'select'}
                  onClick={() => tool === 'select' && setSelectedId(shape.id)}
                  onDragEnd={handleDragEnd}
                  onTransformEnd={handleTransformEnd}
                  rotation={shape.rotation}
                />
              );
            } else if (shape.type === 'image') {
              const imageObj = new window.Image();
              imageObj.crossOrigin = 'anonymous';
              imageObj.src = shape.image;
              return (
                <Image
                  key={shape.id}
                  id={shape.id}
                  image={imageObj}
                  x={shape.x}
                  y={shape.y}
                  width={shape.width}
                  height={shape.height}
                  rotation={shape.rotation}
                  draggable={tool === 'select'}
                  onClick={() => tool === 'select' && setSelectedId(shape.id)}
                  onDragEnd={handleDragEnd}
                  onTransformEnd={handleTransformEnd}
                />
              );
            }
            return null;
          })}
          {selectedId && tool === 'select' && (
            <Transformer
              ref={transformerRef}
              boundBoxFunc={(oldBox, newBox) => {
                const isValid = newBox.width > 5 && newBox.height > 5;
                return isValid ? newBox : oldBox;
              }}
              rotationSnaps={[0, 45, 90, 135, 180, 225, 270, 315]}
              enabledAnchors={[
                'top-left', 'top-center', 'top-right',
                'middle-left', 'middle-right',
                'bottom-left', 'bottom-center', 'bottom-right'
              ]}
              anchorSize={8}
              anchorCornerRadius={4}
              borderStroke="#00ff00"
              anchorStroke="#00ff00"
              anchorFill="#ffffff"
              padding={5}
            />
          )}
        </Layer>
      </Stage>
    </div>
  );
};

export default Whiteboard;
