import React, { useEffect, useRef, useState } from 'react';
import { Circle, Image, Layer, Line, Rect, Stage, Transformer } from 'react-konva';
import io from 'socket.io-client';
// Fix import paths to be within src directory
import circleIcon from '../assets/icons/circle.png';
import clearIcon from '../assets/icons/clear.png';
import eraserIcon from '../assets/icons/eraser.png';
import imageIcon from '../assets/icons/image.png';
import lineIcon from '../assets/icons/line.png';
import penIcon from '../assets/icons/pen.png';
import rectangleIcon from '../assets/icons/rectangle.png';
import selectIcon from '../assets/icons/select.png';
// Add logo import
import logo from '../assets/logo/logo.png';

const socket = io('http://192.168.31.158:3001', {
  transports: ['websocket', 'polling'],
  reconnectionDelay: 1000,
  reconnectionAttempts: 10,
  forceNew: true
});

const Whiteboard = () => {
  const [lines, setLines] = useState([]);
  const [shapes, setShapes] = useState([]);
  const [selectedId, setSelectedId] = useState(null);
  const [tool, setTool] = useState('pen');
  const [isDrawing, setIsDrawing] = useState(false);
  const [startPos, setStartPos] = useState({ x: 0, y: 0 });
  const [selectedColor, setSelectedColor] = useState('#000000');
  const [selectedStrokeWidth, setSelectedStrokeWidth] = useState(5);
  const [showColorPicker, setShowColorPicker] = useState(false);
  const [showStrokePicker, setShowStrokePicker] = useState(false);
  const transformerRef = useRef();
  const stageRef = useRef();
  // Add state for logo
  const [logoImage] = useState(new window.Image());

  const colors = [
    '#000000', '#FF0000', '#00FF00', '#0000FF',
    '#FFFF00', '#FF00FF', '#00FFFF', '#800000',
    '#008000', '#000080', '#808000', '#800080',
    '#008080', '#808080', '#C0C0C0', '#FFFFFF'
  ];

  useEffect(() => {
    socket.on('draw-update', ({ lines, shapes }) => {
      setLines(lines);
      setShapes(shapes);
    });

    return () => {
      socket.off('draw-update');
    };
  }, []);

  useEffect(() => {
    if (selectedId) {
      const shape = shapes.find(s => s.id === selectedId);
      if (shape && transformerRef.current) {
        transformerRef.current.nodes([stageRef.current.findOne('#' + selectedId)]);
        transformerRef.current.getLayer().batchDraw();
      }
    }
  }, [selectedId, shapes]);

  // Add useEffect for logo loading
  useEffect(() => {
    logoImage.src = logo;
  }, [logoImage]);

  const handleMouseDown = (e) => {
    // Detect if it's touch input and get proper coordinates
    const isTouchEvent = e.type.includes('touch');
    let pos;
    
    if (isTouchEvent) {
      e.preventDefault(); // Prevent scrolling on touch devices
      const touch = e.touches[0];
      const stage = e.target.getStage();
      // Get proper coordinates relative to the stage
      const point = stage.getPointerPosition() || {
        x: touch.clientX - stage.container().offsetLeft,
        y: touch.clientY - stage.container().offsetTop
      };
      pos = point;
    } else {
      pos = e.target.getStage().getPointerPosition();
    }

    if (!pos) return; // Guard clause to prevent undefined positions

    setIsDrawing(true);
    setStartPos(pos);

    if (tool === 'pen') {
      const newLines = [...lines, { 
        tool,
        points: [pos.x, pos.y],
        color: selectedColor,
        strokeWidth: selectedStrokeWidth,
        tension: 0.4,
        lineCap: 'round',
        lineJoin: 'round'
      }];
      setLines(newLines);
      socket.emit('draw-update', { lines: newLines, shapes });
    } else if (tool === 'eraser') {
      const newLines = [...lines, { 
        tool, 
        points: [pos.x, pos.y],
        color: '#ffffff',
        strokeWidth: 20 
      }];
      setLines(newLines);
      socket.emit('draw-update', { lines: newLines, shapes });
    } else if (tool === 'select') {
      const clickedShape = e.target;
      if (clickedShape === e.target.getStage()) {
        setSelectedId(null);
        return;
      }
      if (clickedShape.getParent().className === 'Transformer') {
        return;
      }
      setSelectedId(clickedShape.id());
    }
  };

  const handleMouseMove = (e) => {
    if (!isDrawing) return;

    // Prevent default scrolling on touch devices
    if (e.type.includes('touch')) {
      e.preventDefault();
      const touch = e.touches[0];
      const stage = e.target.getStage();
      const point = stage.getPointerPosition() || {
        x: touch.clientX - stage.container().offsetLeft,
        y: touch.clientY - stage.container().offsetTop
      };

      if (tool === 'pen') {
        const lastLine = lines[lines.length - 1];
        if (!lastLine) return;

        // Simple point addition with error checking
        const newPoints = [...lastLine.points, point.x, point.y];
        
        const newLines = [...lines.slice(0, -1), {
          ...lastLine,
          points: newPoints,
          tension: 0.4,
          strokeWidth: selectedStrokeWidth,
          color: selectedColor
        }];

        setLines(newLines);
        socket.emit('draw-update', { lines: newLines, shapes });
      }
      // ...rest of your existing handleMouseMove code...
    } else {
      const point = e.target.getStage().getPointerPosition();
      if (tool === 'pen') {
        const lastLine = lines[lines.length - 1];
        // Simple point addition without complex smoothing
        lastLine.points = lastLine.points.concat([point.x, point.y]);

        const newLines = [...lines.slice(0, -1), {
          ...lastLine,
          tension: 0.4,
          strokeWidth: selectedStrokeWidth,
          color: selectedColor
        }];

        setLines(newLines);
        socket.emit('draw-update', { lines: newLines, shapes });
      } else {
        const newShapes = [...shapes];
        const index = newShapes.findIndex(s => s.id === 'temp');
        const shapeData = {
          id: 'temp',
          type: tool,
          x: startPos.x,
          y: startPos.y,
          width: point.x - startPos.x,
          height: point.y - startPos.y,
          color: selectedColor,
          strokeWidth: selectedStrokeWidth
        };
        
        if (index === -1) {
          newShapes.push(shapeData);
        } else {
          newShapes[index] = shapeData;
        }
        setShapes(newShapes);
        socket.emit('draw-update', { lines, shapes: newShapes });
      }
    }
  };

  const handleMouseUp = () => {
    setIsDrawing(false);
    if (['line', 'rectangle', 'circle'].includes(tool)) {
      const newShapes = shapes.map(shape => 
        shape.id === 'temp' ? { ...shape, id: Date.now().toString() } : shape
      );
      setShapes(newShapes);
      socket.emit('draw-update', { lines, shapes: newShapes });
    }
  };

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (!file) return;
  
    if (!file.type.startsWith('image/')) {
      alert('Please upload an image file');
      return;
    }
  
    const reader = new FileReader();
    reader.onload = () => {
      const tempCanvas = document.createElement('canvas');
      const tempCtx = tempCanvas.getContext('2d');
      const img = new window.Image();
      
      img.onload = () => {
        // Set fixed width of 300px and calculate height to maintain aspect ratio
        const targetWidth = 300;
        const aspectRatio = img.height / img.width;
        const targetHeight = targetWidth * aspectRatio;
  
        // Set canvas size and draw image
        tempCanvas.width = targetWidth;
        tempCanvas.height = targetHeight;
        tempCtx.drawImage(img, 0, 0, targetWidth, targetHeight);
        
        const processedImageData = tempCanvas.toDataURL('image/png');
  
        const newShape = {
          id: Date.now().toString(),
          type: 'image',
          x: window.innerWidth / 4,
          y: window.innerHeight / 4,
          width: targetWidth,
          height: targetHeight,
          image: processedImageData,
          rotation: 0,
        };
  
        const newShapes = [...shapes, newShape];
        setShapes(newShapes);
        socket.emit('draw-update', { lines, shapes: newShapes });
      };
      img.src = reader.result;
    };
    reader.readAsDataURL(file);
  };

  const handleDragEnd = (e) => {
    const shape = e.target;
    const newShapes = shapes.map(s => {
      if (s.id === shape.id()) {
        if (s.type === 'circle') {
          return {
            ...s,
            x: shape.x() - s.width / 2,
            y: shape.y() - s.height / 2
          };
        }
        return {
          ...s,
          x: shape.x(),
          y: shape.y()
        };
      }
      return s;
    });
    setShapes(newShapes);
    socket.emit('draw-update', { lines, shapes: newShapes });
  };

  const handleTransformEnd = (e) => {
    const node = e.target;
    const scaleX = node.scaleX();
    const scaleY = node.scaleY();
    
    node.scaleX(1);
    node.scaleY(1);
    
    const newShapes = shapes.map(shape => 
      shape.id === node.id() ? {
        ...shape,
        x: node.x(),
        y: node.y(),
        width: Math.abs(node.width() * scaleX),
        height: Math.abs(node.height() * scaleY),
        rotation: node.rotation()
      } : shape
    );
    setShapes(newShapes);
    socket.emit('draw-update', { lines, shapes: newShapes });
  };

  return (
    <div className="whiteboard-container">
      <div style={{ 
        position: 'fixed', 
        left: 10, 
        top: '50%', 
        transform: 'translateY(-50%)',
        display: 'flex',
        flexDirection: 'column',
        gap: '8px',
        backgroundColor: 'rgba(255, 255, 255, 0.8)',
        padding: '10px',
        borderRadius: '8px',
        zIndex: 1,
      }}>
        <button 
          onClick={() => setTool('pen')} 
          style={{ 
            padding: 8,
            backgroundColor: 'transparent',
            border: '1px solid #333',
            borderRadius: '4px',
            cursor: 'pointer',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            width: '40px',
            height: '40px'
          }}
        >
          <img src={penIcon} alt="Pen" width="24" height="24" />
        </button>
        {/* Apply the same style to other tool buttons */}
        <button onClick={() => setTool('eraser')} style={{ /* same button style */ }}>
          <img src={eraserIcon} alt="Eraser" width="24" height="24" />
        </button>
        <button onClick={() => setTool('line')} style={{ /* same button style */ }}>
          <img src={lineIcon} alt="Line" width="24" height="24" />
        </button>
        <button onClick={() => setTool('rectangle')} style={{ /* same button style */ }}>
          <img src={rectangleIcon} alt="Rectangle" width="24" height="24" />
        </button>
        <button onClick={() => setTool('circle')} style={{ /* same button style */ }}>
          <img src={circleIcon} alt="Circle" width="24" height="24" />
        </button>
        <button onClick={() => setTool('select')} style={{ /* same button style */ }}>
          <img src={selectIcon} alt="Select" width="24" height="24" />
        </button>

        {/* Color Picker */}
        <div style={{ display: 'inline-block', position: 'relative' }}>
          <button 
            onClick={() => setShowColorPicker(!showColorPicker)}
            style={{ 
              backgroundColor: selectedColor, 
              width: '40px',
              height: '40px',
              border: '1px solid #333',
              borderRadius: '4px',
              cursor: 'pointer'
            }}
          />
          {showColorPicker && (
            <div style={{
              position: 'absolute',
              top: '100%',
              left: '100%',
              backgroundColor: 'white',
              border: '1px solid #ccc',
              padding: 5,
              display: 'grid',
              gridTemplateColumns: 'repeat(4, 1fr)',
              gap: 5,
              zIndex: 2,
              marginLeft: '10px',
            }}>
              {colors.map((color, i) => (
                <div
                  key={i}
                  style={{
                    width: 25,
                    height: 25,
                    backgroundColor: color,
                    cursor: 'pointer',
                    border: '1px solid #ccc'
                  }}
                  onClick={() => {
                    setSelectedColor(color);
                    setShowColorPicker(false);
                  }}
                />
              ))}
            </div>
          )}
        </div>

        {/* Stroke Width Picker */}
        <div style={{ display: 'inline-block', position: 'relative' }}>
          <button 
            onClick={() => setShowStrokePicker(!showStrokePicker)}
            style={{ 
              width: '40px',
              height: '40px',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              border: '1px solid #333',
              borderRadius: '4px',
              backgroundColor: 'transparent',
              cursor: 'pointer'
            }}
          >
            <div style={{
              width: '80%',
              height: selectedStrokeWidth,
              backgroundColor: selectedColor,
              margin: 'auto'
            }}/>
          </button>
          {showStrokePicker && (
            <div style={{
              position: 'absolute',
              top: '100%',
              left: '100%',
              backgroundColor: 'white',
              border: '1px solid #ccc',
              padding: 5,
              display: 'flex',
              flexDirection: 'column',
              gap: 5,
              zIndex: 2,
              marginLeft: '10px',
            }}>
              {[2, 4, 6, 8, 10, 12, 14, 16].map((width) => (
                <div
                  key={width}
                  style={{
                    width: '100px',
                    height: '20px',
                    cursor: 'pointer',
                    border: '1px solid #ccc',
                    display: 'flex',
                    alignItems: 'center',
                    padding: '0 5px',
                  }}
                  onClick={() => {
                    setSelectedStrokeWidth(width);
                    setShowStrokePicker(false);
                  }}
                >
                  <div style={{
                    width: '100%',
                    height: width,
                    backgroundColor: selectedColor,
                  }}/>
                </div>
              ))}
            </div>
          )}
        </div>

        {/* Image Upload */}
        <div style={{ position: 'relative' }}>
          <input 
            type="file" 
            accept="image/*" 
            onChange={handleImageUpload}
            style={{ 
              width: '40px',
              height: '40px',
              opacity: 0,
              position: 'absolute',
              left: 0,
              top: 0,
              cursor: 'pointer',
              zIndex: 1
            }}
          />
          <button style={{ 
            width: '40px',
            height: '40px',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            border: '1px solid #333',
            borderRadius: '4px',
            backgroundColor: 'transparent',
            cursor: 'pointer'
          }}>
            <img src={imageIcon} alt="Upload Image" width="24" height="24" />
          </button>
        </div>

        <button 
          onClick={() => {
            setLines([]);
            setShapes([]);
            socket.emit('draw-update', { lines: [], shapes: [] });
          }} 
          style={{ 
            width: '40px',
            height: '40px',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            border: '1px solid #333',
            borderRadius: '4px',
            backgroundColor: 'transparent',
            cursor: 'pointer'
          }}
        >
          <img src={clearIcon} alt="Clear All" width="24" height="24" />
        </button>
      </div>

      <Stage
        ref={stageRef}
        width={window.innerWidth}
        height={window.innerHeight}
        onMouseDown={handleMouseDown}
        onMouseMove={handleMouseMove}
        onMouseUp={handleMouseUp}
        onTouchStart={handleMouseDown}
        onTouchMove={handleMouseMove}
        onTouchEnd={handleMouseUp}
        onContextMenu={e => e.preventDefault()}
        listening={true}
      >
        <Layer>
          {/* Move logo to right side */}
          <Image
            image={logoImage}
            x={window.innerWidth - 110} // 10px padding from right edge
            y={10}
            width={100}
            height={40}
            opacity={1}
          />
          {lines.map((line, i) => (
            <Line
              key={i}
              points={line.points}
              stroke={line.color}
              strokeWidth={line.strokeWidth}
              tension={0.4}
              lineCap="round"
              lineJoin="round"
              globalCompositeOperation={
                line.tool === 'eraser' ? 'destination-out' : 'source-over'
              }
            />
          ))}
          {/* Rest of the shapes rendering */}
          {shapes.map((shape) => {
            if (shape.type === 'line') {
              return (
                <Line
                  key={shape.id}
                  id={shape.id}
                  points={[shape.x, shape.y, shape.x + shape.width, shape.y + shape.height]}
                  stroke={shape.color || selectedColor}
                  strokeWidth={shape.strokeWidth || selectedStrokeWidth}
                  draggable={tool === 'select'}
                  onClick={() => setSelectedId(shape.id)}
                  onTransformEnd={handleTransformEnd}
                />
              );
            } else if (shape.type === 'rectangle') {
              return (
                <Rect
                  key={shape.id}
                  id={shape.id}
                  x={shape.x}
                  y={shape.y}
                  width={shape.width}
                  height={shape.height}
                  rotation={shape.rotation}
                  stroke={shape.color || selectedColor}
                  strokeWidth={shape.strokeWidth || selectedStrokeWidth}
                  draggable={tool === 'select'}
                  onClick={() => tool === 'select' && setSelectedId(shape.id)}
                  onDragEnd={handleDragEnd}
                  onTransformEnd={handleTransformEnd}
                />
              );
            } else if (shape.type === 'circle') {
              return (
                <Circle
                  key={shape.id}
                  id={shape.id}
                  x={shape.x + shape.width / 2}
                  y={shape.y + shape.height / 2}
                  radius={Math.abs(shape.width) / 2}
                  stroke={shape.color || selectedColor}
                  strokeWidth={shape.strokeWidth || selectedStrokeWidth}
                  draggable={tool === 'select'}
                  onClick={() => tool === 'select' && setSelectedId(shape.id)}
                  onDragEnd={handleDragEnd}
                  onTransformEnd={handleTransformEnd}
                  rotation={shape.rotation}
                />
              );
            } else if (shape.type === 'image') {
              const imageObj = new window.Image();
              imageObj.crossOrigin = 'anonymous';
              imageObj.src = shape.image;
              return (
                <Image
                  key={shape.id}
                  id={shape.id}
                  image={imageObj}
                  x={shape.x}
                  y={shape.y}
                  width={shape.width}
                  height={shape.height}
                  rotation={shape.rotation}
                  draggable={tool === 'select'}
                  onClick={() => tool === 'select' && setSelectedId(shape.id)}
                  onDragEnd={handleDragEnd}
                  onTransformEnd={handleTransformEnd}
                />
              );
            }
            return null;
          })}
          {selectedId && tool === 'select' && (
            <Transformer
              ref={transformerRef}
              boundBoxFunc={(oldBox, newBox) => {
                const isValid = newBox.width > 5 && newBox.height > 5;
                return isValid ? newBox : oldBox;
              }}
              rotationSnaps={[0, 45, 90, 135, 180, 225, 270, 315]}
              enabledAnchors={[
                'top-left', 'top-center', 'top-right',
                'middle-left', 'middle-right',
                'bottom-left', 'bottom-center', 'bottom-right'
              ]}
              anchorSize={8}
              anchorCornerRadius={4}
              borderStroke="#00ff00"
              anchorStroke="#00ff00"
              anchorFill="#ffffff"
              padding={5}
            />
          )}
        </Layer>
      </Stage>
    </div>
  );
};

export default Whiteboard;
